INSERT INTO `tin_tuyen_dung` (`id`, `tieu_de`, `mo_ta`, `yeu_cau`, `muc_luong`) VALUES
(3, 'Manual Tester', 'Tham gia và nghiên cứu các phần mềm thuộc dự án đang làm.\r\nLập kế hoạch kiểm thử, viết testcase cho việc kiểm thử.\r\nLog bug, đánh giá mức độ nghiêm trọng của lỗi và theo dõi trạng thái lỗi.\r\nChịu trách nhiệm về việc đo lường, phân tích và báo cáo dữ liệu nhằm đánh giá chất lượng sản phẩm.\r\nGiao tiếp với Designer và Developer cùng các bên liên quan nội bộ khác để làm rõ các yêu cầu.\r\nHoàn thành các công việc theo yêu cầu của quản lý.', 'Có thể làm việc FULL TIME\r\nSinh viên năm cuối ngành CNTT và các ngành khác liên quan.\r\nCó kiến thức nền tảng về các công cụ test và phương pháp test.\r\nƯu tiên các ứng viên đã làm các dự án, sử dụng cả web và mobile.\r\nƯu tiên các ứng viên có hướng lên PO hoặc BA.\r\nNăng động, chăm chỉ, tỉ mỉ và có tinh thần trách nhiệm cao trong công việc\r\nCó khả năng làm việc độc lập và làm việc nhóm tốt.\r\nƯu tiên ứng viên có thể đọc hiểu tiếng Anh.\r\nCó thể gắn bó lâu dài với công ty.', '8.000.000đ - 12.000.000đ / tháng'),
(4, 'Tester Jobs Definition', 'Lập kế hoạch test, tạo test case và thực hiện test cho các website/app.\r\nQuản lý bug và tiến độ fix bug của dev, thực hiện kiểm tra lại và kiểm tra hồi quy.\r\nBiên soạn tài liệu phục vụ cho việc test, phân tích kết quả test, báo cáo kết quả test. \r\nQuản lý tiến độ test, chất lượng sản phẩm.', 'Có từ 2-3 năm kinh nghiệm trở lên ở vị trí tương đương.\r\nCó khả năng đọc hiểu tài liệu, phân tích yêu cầu, có khả năng tư duy logic.\r\nCó khả năng viết báo cáo.\r\nHiểu biết về các giai đoạn kiểm thử (Unit Test, Integration Test, System Test v.v...), và các kỹ thuật kiểm thử phần mềm.\r\nThành thạo Google Spreadsheet, google document.\r\nỨng viên có tiếng Nhật là 1 lợi thế.\r\nNăng động, nhiệt tình, chăm chỉ, có trách nhiệm với công việc.\r\n', 'Thỏa thuận'),
(5, 'Test Game', 'Kiểm tra chất lượng các sản phẩm game của công ty\r\nPhát hiện và gửi báo cáo về các lỗi, sự cố và các vấn đề khác trong quá trình chơi game.\r\nLàm công việc do leader giao.', 'Chăm chỉ, tỉ mỉ, cẩn thận\r\nCó khả năng phát hiện ra các lỗi trong Game\r\nCó khả năng làm việc nhóm và giao tiếp tốt.\r\nƯu tiên tuyển nữ, tỉ mỉ, cẩn thận.\r\nKhông yêu cầu kinh nghiệm, được đào tạo từ đầu', '9 - 10 triệu'),
(6, 'PHP Developer', 'Tham gia vào phát triển các dự án với vai trò developer từ giai đoạn Basic Design, Detail Design, Development, Release. \r\nLàm các công việc khác theo sự phân công của quản lý( Research new Technologies, Support team…) ', 'Có ít nhất 2 năm kinh nghiệm làm việc với PHP công ty Outsource\r\nCó kiến thức nền tảng tốt về OOP, Web Development, DBMS, ORM, Design Pattern \r\nCó kinh nghiệm sử dụng một trong các Framework PHP: CakePHP, Yii, Laravel \r\nThao tác tốt với HTML, CSS, JavaScript (Jquery, AngularJS, ...), Bootstrap, JSON, XML,.. \r\nCó kinh nghiệm làm việc với MySQL hoặc NoSQL (MongoDB) \r\nNắm vững mô hình MVC \r\nThành thạo Git', 'Thỏa thuận'),
(7, 'ReactJS Developer', 'Tham gia các dự án sử dụng ngôn ngữ lập trình ReactJS.…và các công nghệ liên quan cho các khách hàng đến từ Nhật Bản. \r\nPhân tích thiết kế, lập trình và viết UT cho các chức năng của các hệ thống nghiệp vụ hoặc webapp. \r\nThực hiện UI của ứng dụng web hiện đại, giàu tương tác với yêu cầu cao về chất lượng và độ tin cậy \r\nThảo luận và tư vấn về API backend \r\nNghiên cứu công nghệ thích hợp để giải quyết vấn đề \r\nLàm việc vs các CMS như wordpress ', 'Tốt nghiệp Đại học, Cao đẳng chuyên ngành CNTT. \r\nTối thiểu 1 năm kinh nghiệm Frontend (Reactjs, Next.js, TypeScript)\r\nThành thạo HTML5, CSS3, JS, Jquery \r\nCó kiến thức về responsive design, cross browser compatibility \r\nCó kinh nghiệm sử dụng git \r\nOptional: Tailwind, CSS (thành thạo là một lợi thế)', 'Thỏa thuận');
